INSERT INTO `entries` (`id`, `uri`, `title`, `content`, `author`, `date`, `year`, `month`, `day`) VALUES (NULL, 'ut_enim_obscuratur_et_offunditu', 'Ut Enim Obscuratur Et Offunditu?', 'Ut vero conservetur omnis homini erga hominem societas, coniunctio, caritas, et emolumenta et detrimenta, quae etappellant, communia esse voluerunt; quorum altera prosunt, nocent altera. Neque solum ea communia, verum etiam pariaesse dixerunt. Incommoda autem et commoda - ita enim et appello - communia esse voluerunt, parianoluerunt. Illa enim, quae prosunt aut quae nocent, aut bona sunt aut mala, quae sint paria necesse est. Commoda autemet incommoda in eo genere sunt, quae praeposita et reiecta diximus; ea possunt paria non esse.', '1', '2011-4-3', '2011', '4', '3');
